        <div className="col-md-4 col-lg-3 col-sm-6 col-xs-12 col-xl-2">
            <div className="mt-3">
                <div className="card" style={{"width": "20rem"}}>
                    <div className="card-body">
                        <h5 className="card-title text-secondary">{}</h5>
                        <p className="card-text">Some description.

                            <img className="card-img-top" alt="Card image cap"
                                 src={`https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSqzaE-yVJFUm_0dmcYTWdYn-Oe-0rFaxi76A&usqp=CAU`}/>
                        </p>
                    </div>
                </div>
            </div>
            {/*{JSON.stringify(updatePuppy)}*/}
        </div>

    )
}

const stpm = (state) =>({

    pets: state.petsReducer.pets
})

const dtpm = (dispatch) =>({

    findPetForUser: (userId) => {
        petService.findPetForUser(userId)
            .then(pets => dispatch({
                type: "FIND_PET",
                pets
            }))
    },

    createPet: (userId) => {
        petService.createPet(userId, {name:"new pet", breed:"new breed", status:"current status"})
            .then(pet => dispatch ({
                type: "CREATE_PET",
                pet
            }))
    },
    deletePet: (petId) => {
        petService.deletePet(petId)
            .then(status => dispatch({
                type: "DELETE_PET",
                petToDelete: petId
            }))
    },

    updatePet: (petId, pet) => {
        petService.updatePet(petId, pet)
            .then(status => dispatch({
                type: "UPDATE_PET",
                pet: pet
            }))
    },

    findPetForId:(petId) => {
        petService.findPetForId(petId)
            .then(pet => dispatch({
                type: "FIND_PET_FOR_PETID",
                pet: pet
            }))
    }

})

export default connect(stpm, dtpm)(PetCard)

