import React, { useState, useEffect } from "react";
import {Link, useHistory, useParams} from 'react-router-dom'
import newUser from "./login"
import userService from "../../services/./search-services/user-service";

const Profile =({updateUser, findUserForUsername,user={}, show = false})=> {
    const {userId} = useParams()
    const [edited, setEdited] = useState(false);
    const [changeUser, setChangeUser] = useState(user);
    const [contact, setContact] = useState({})
    const  history = useHistory();
    const [shows, setShows] = useState(false)

    useEffect(() => {
        if (changeUser !== {}) {
            console.log(shows)
            setShows(true)
            console.log("true1")
            console.log(changeUser)
        }else {
            console.log(shows)
            findUserByUserId(userId)
        }
    }, [userId])

    const findUserByUserId = (userId) => {
        userService.findUserByUserId(userId)
            .then((data) => {
                setContact(data)
                console.log("data")
                console.log(data)
                console.log("contact")
                console.log(contact)
                console.log("updatepuppy")
                console.log(changeUser)
                console.log(shows)
            })
        console.log(contact)
    }

    // useEffect(() => {
