import React, {useState} from 'react'
import Login from "./login/login"
import Profile from "./login/profile"
import Register from "./login/register"
import {BrowserRouter as Router, Link, Route, Switch} from "react-router-dom";
import userService from "../services/user-service";



class UserManagement extends React.Component {



    state = {
        user :[],
        users:[]
    }
    componentDidMount=() => {

        userService.findAllUsers()
            .then(users => this.setState({users:users}))
        console.log(this.state.users)
    }
    findUserForUsername =(user) => {
        userService.findUserForUsername(user.username)
            .then((newUser) => {this.setState(
                {user:newUser})})
        console.log(this.state)
    }


    addUser=(user) => {

        userService.createUser(user)
            .then(user =>this.setState(
                (prevState) => ({
                    ...prevState,
                    users: [
                        ...prevState.users,
                        user
                    ]
                })))
    }





    deleteUser = (courseToDelete) => {
        userService.deleteUser(courseToDelete.userId)
            .then(status => {
                this.setState((prevState) =>{
                    let nextState = {
                        ...prevState,
                        users:  prevState
                            .users
                            .filter(user => user !== courseToDelete)
                    }

                    return nextState
                })
            })
    }

    updateUser = (user) => {
        userService.updateUser(user.userId, user)
            .then(status => this.setState((prevState) =>({
                ...prevState,
                users: prevState.users.map(
                    c =>c.userId === user.userId ? user : c)
            })))

    }
    render() {
        return (


                <div>
                    <i onClick={() => this.componentDidMount()} className="fas fa-plus float-right"></i>
                <Route path="/" exact={true}>
                    <Login
                        findUserForUsername = {this.findUserForUsername}
                        componentDidMount = {this.componentDidMount}
                        users={this.state.users} />
                </Route>


                <Route path="/login" exact={true}>
                    <Login

                        findUserForUsername = {this.findUserForUsername}
                        users={this.state.users} />
                </Route>
                <Route path="/register" exact={true}>
                    <Register
                        updateUser = {this.updateUser}
                        deleteUser={this.deleteUser}
                        users={this.state.users} />
                </Route>
                <Route path="/profile" exact={true}>
                    <Profile
                        findUserForUsername = {this.findUserForUsername}
                        updateUser={this.updateUser}
                        user={this.state.user} />
                </Route>
                {/*<Route path="/" exact={true}>*/}
                {/*    <Login*/}
                {/*        />*/}
                {/*</Route>*/}


                {/*<Route path="/login" exact={true}>*/}
                {/*    <Login*/}
                {/*        />*/}
                {/*</Route>*/}
                {/*<Route path="/register" exact={true}>*/}
                {/*    <Register*/}
                {/*       />*/}
                {/*</Route>*/}
                {/*<Route path="/profile" exact={true}>*/}
                {/*    <Profile*/}
                {/*       />*/}
                {/*</Route>*/}




            </div>
        )
    }



}


export default UserManagement
