import React from 'react'

const initialState = {

    pets:[]
}

const petsReducer = (state=initialState, action) => {
    switch (action.type) {
        case "CREATE_PET":
            return {
                ...state,
                pets: [
                    ...state.pets,
                    action.pet
                ]
            }
        case "FIND_PET":
            return {
                ...state,
                pets: action.pets
            }
        case "DELETE_PET":
            return {

                pets: state.pets.filter(pet => pet.id !== action.petToDelete)
            }

        case "UPDATE_PET":
            return {

                pets: state.pets.map(pet => pet.id !== action.pet._id ? pet : action.pet)
            }

        default:
            return state
    }
}

export default petsReducer
