import axios from "axios";

// const API_URL = "http://localhost:8080/api/users";
const API_URL = "https://petfinderserver.herokuapp.com/api/pets";

export const findAllPets = () =>
    fetch(API_URL)
        .then(response => response.json())

export const createPet = (userId, pet) =>
    fetch(`${API_URL}/${userId}/pet`, {
        method: 'POST',
        body: JSON.stringify(pet),
        headers:{
            'content-type': 'application/json'
        }
    })
        .then(response => response.json())

export const deleteUser = (petId) =>
    fetch(`${API_URL}/${petId}`, {
        method:'DELETE',
    })
        .then(response => response.json())


export const deletePet = (petId, pet) =>
    fetch(`${API_URL}/${petId}`, {
        method: 'PUT',
        credentials: 'same-origin',
        body: JSON.stringify(pet),
        headers:{
            'content-type': 'application/json'
        }
    })
        .then(response => response.json())

export const findPetForUser= (userId) =>
    fetch(`${API_URL}/${userId}/pet`)
        .then(response =>response.json())





export default {
    createPet,
    deletePet,
    updatePet,
    findPetForUser,
    findAllPets
};
