                    </tbody>
                </table>
            </div>
        )
    }

const stpm = (state) =>({

    pets: state.petsReducer.pets
})

const dtpm = (dispatch) =>({

    findPetForUser: (userId) => {
        petService.findPetForUser(userId)
            .then(pets => dispatch({
                type: "FIND_PET",
                pets
            }))
    },

    createPet: (userId) => {
        petService.createPet(userId, {name:"new pet", breed:"new breed", status:"current status"})
            .then(pet => dispatch ({
                type: "CREATE_PET",
                pet
            }))
    },
    deletePet: (petId) => {
        petService.deletePet(petId)
            .then(status => dispatch({
                type: "DELETE_PET",
                petToDelete: petId
            }))
    },

    updatePet: (petId, pet) => {
        petService.updatePet(petId, pet)
            .then(status => dispatch({
                type: "UPDATE_PET",
                pet: pet
            }))
    },

    findPetForId:(petId) => {
        petService.findPetForId(petId)
            .then(pet => dispatch({
                type: "FIND_PET_FOR_PETID",
                pet: pet
            }))
    }

})

export default connect(stpm, dtpm)(PostTable)

