import React, {useEffect, useState} from "react"
import {Link, useParams} from "react-router-dom";
import petService from "../../services/pet-service";
import {connect} from "react-redux";

const PetCard = (
    {
        // pet = {},
        // findPetForId,
        // deletePet,
        // updatePet,
    }) => {

    const {petId} = useParams()
    const [updatePuppy, setUpdatePuppy] = useState({})
    const [edited, setEdited] = useState(false);

    useEffect(() => {
        console.log(petId)
        // if(petId !== "undefined" && typeof petId !== "undefined") {

        findPetForId(petId)
        // setUpdatePuppy(pet)
        console.log("setup")
        // console.log(pet)
        console.log(updatePuppy)

    }, [petId])

    const findPetForId = (petId) => {
        petService.findPetForId(petId)
            .then((pet) => {
                setUpdatePuppy(pet)
            })
    }

    const deletePet = (petId) => {
        petService.deletePet(petId)
            // .then(status => dispatch({
            //     type: "DELETE_PET",
            //     petToDelete: petId
            // }))
    }

    const updatePet = (petId, pet) => {
        petService.updatePet(petId, pet)
            .then((pet) => {
                setUpdatePuppy(pet)
            })
    }

    return (
        <div>
            {
            edited &&

            <form>

                    <i onClick={() => {
                        findPetForId(petId)
                        updatePet(petId, updatePuppy)
                        setEdited(false)
                        findPetForId(petId)
                    }} className="fas fa-check float-right"></i>

                    <i onClick={() => deletePet(petId)} className="fas fa-trash float-right"></i>
                    <div className="form-group">
                        <label>Name</label>
                        <textarea  className="form-control" value={updatePuppy.name}
                                   onChange={(e) =>
                                       setUpdatePuppy({
                                           ...updatePuppy,
                                           name:e.target.value
                                       })}
                        >{updatePuppy.name}</textarea>
                     </div>

                    <div className="form-group">
                        <label>Breed</label>
                        <textarea  className="form-control" value={updatePuppy.breed}
                                   onChange={(e) =>
                                       setUpdatePuppy({
                                           ...updatePuppy,
                                           breed:e.target.value
                                       })}
                        >{updatePuppy.breed}</textarea>
                    </div>


            </form>
            }

            {
                !edited &&
                <>
                    <i onClick={() => setEdited(true)} className="fas fa-cog float-right"> </i>
                    <form>


                        <div className="form-group">
                            <label>Name</label>
                            <textarea  className="form-control" value={updatePuppy.name}>{updatePuppy.name}</textarea>
                        </div>
                        <div className="form-group">
                            <label>Breed</label>
                            <textarea  className="form-control" value={updatePuppy.breed}>{updatePuppy.breed}</textarea>
                        </div>
                        <div className="form-group">
                            <label>Name</label>
                            <textarea  className="form-control" value={updatePuppy.name}>{updatePuppy.name}</textarea>
                        </div>
                        <div className="form-group">
                            <label>Breed</label>
                            <textarea  className="form-control" value={updatePuppy.breed}>{updatePuppy.breed}</textarea>
                        </div>

                    </form>

                </>
            }
            {JSON.stringify(updatePuppy)}
    </div>

)
}

export default PetCard
