                {/*                      ...changeUser,*/}
                {/*                      password:e.target.value*/}
                {/*                  })}*/}
                {/*              placeholder="Password" />*/}
                {/*</div>*/}
                <Link to="/login">
                    <button className="btn btn-primary btn-block"
                            onClick={() => {
                                // findUserForUsername(changeUser)
                                resetUser(changeUser)

                            }}
                    >Update</button>
                </Link>

            </form>
        );
    }
