import React, { useState, useEffect } from "react";
import {Link} from "react-router-dom";
import newUser from "./login"

const Profile =({updateUser, findUserForUsername,user})=> {
    const [edited, setEdited] = useState(false);
    const [changeUser, setChangeUser] = useState(user);

    // useEffect(() => {
    //     // findUserForUsername(changeUser)
    //     console.log(changeUser)
    // }, [user])
        return (
            <div>
                {
                    changeUser && changeUser[0] && changeUser[0].username &&

                    <form>
                        <h3>Profile</h3>
                        {JSON.stringify(changeUser)}

                        <div className="form-group">
                            <label>User Name</label>
                            <textarea type="text" className="form-control" value={changeUser.username}
                                      onChange={(e) =>
                                          setChangeUser({
                                              ...changeUser,
                                              username:e.target.value
                                          })}
                                      placeholder="User name">{changeUser[0].username}</textarea>
                        </div>

                        <div className="form-group">
                            <label>First name</label>
                            <textarea type="text" className="form-control" placeholder="First name">{changeUser[0].firstName}</textarea>
                        </div>

                        <div className="form-group">
                            <label>Last name</label>
                            <textarea type="text" className="form-control" placeholder="Last name">{changeUser[0].lastName}</textarea>
                        </div>

                        <div className="form-group">
                            <label>Email address</label>
                            <textarea type="email" className="form-control" placeholder="Enter email">{changeUser[0].email}</textarea>
                        </div>

                        <div className="form-group">
                            <label>Password</label>
                            <textarea type="number" className="form-control" placeholder="Password">{changeUser[0].password}</textarea>
                        </div>
                        <Link to="/profile">
                            <button className="btn btn-primary btn-block"
                                    onClick={() => {
                                        findUserForUsername(changeUser)
                                        updateUser(changeUser[0].userId, changeUser)
                                        findUserForUsername(changeUser)
                                    }}>Update</button>
                        </Link>
                        <br/>

                        <Link to="/petlist">
                            <button className="btn btn-primary btn-block">See your post</button>
                        </Link>
                    </form>
                }

            {
                !changeUser[0] &&

                <form>
                <h3>Profile</h3>
                {/*<div className="form-group">*/}
                {/*    <label>User type</label>*/}
                {/*    <select type="text" className="form-select form-control" aria-label="Default select example">*/}
                {/*        <option selected>User type</option>*/}
                {/*        <option value="1">User</option>*/}
                {/*        <option value="2">Admin</option>*/}
                {/*    </select>*/}
                {/*</div>*/}

                <div className="form-group">
                    <label>User Name</label>
                    <textarea type="text" className="form-control" placeholder="User Name" />
                </div>

                <div className="form-group">
                    <label>First name</label>
                    <textarea type="text" className="form-control" placeholder="First name" />
                </div>

                <div className="form-group">
                    <label>Last name</label>
                    <textarea type="text" className="form-control" placeholder="Last name" />
                </div>

                <div className="form-group">
                    <label>Email address</label>
                    <textarea type="email" className="form-control" placeholder="Enter email" />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <textarea type="number" className="form-control" placeholder="Password" />
                </div>
                <Link to="/profile">
                    <button className="btn btn-primary btn-block"
                            onClick={() => {
                                findUserForUsername(changeUser)
                                updateUser(changeUser)
                                findUserForUsername(changeUser)
                            }}>Update</button>
                </Link>
                <br/>

                <Link to="/petlist">
                    <button className="btn btn-primary btn-block">See your post</button>
                </Link>
            </form>
            }
            </div>
        )
    }

export default Profile
