import axios from "axios";
import {resetFirstInputPolyfill} from "web-vitals/dist/modules/lib/polyfills/firstInputPolyfill";

// const API_URL = "http://localhost:8080/api/pets";
const API_URL = "https://petfinderserver.herokuapp.com/api";

export const findAllPets = () =>
    fetch(API_URL)
        .then(response => response.json())



export const createPet = (userId, pet) =>
    fetch(`${API_URL}/${userId}/pet`, {
        method: 'POST',
        body: JSON.stringify(pet),
        headers:{
            'content-type': 'application/json'
        }
    })
        .then(response => response.json())

export const deletePet = (petId) =>
    fetch(`${API_URL}/pets/${petId}`, {
        method:'DELETE',
    })
        .then(response => response.json())


export const updatePet = (petId, pet) =>
    fetch(`${API_URL}/pets/${petId}`, {
        method: 'PUT',
        credentials: 'same-origin',
        body: JSON.stringify(pet),
        headers:{
            'content-type': 'application/json'
        }
    })
        .then(response => response.json())

export const findPetForUser= (userId) =>
    fetch(`${API_URL}/pets/${userId}/pet`)
        .then(response =>response.json())

export const findPetForId=(petId) =>
    fetch(`${API_URL}/pet/${petId}`)
        .then(response => response.json())
const PET_URL="https://petfinderserver.herokuapp.com/api"
const findPetById = (petId)=>{
    return fetch(`${PET_URL}/pet/${petId}`).then(res=>res.json())
}
// export const findPetForId=(petId) =>
//     fetch(`http://localhost:8080/api/pet/${petId}`)
//         .then(response => response.json())







export default {
    createPet,
    deletePet,
    updatePet,
    findPetForUser,
    findAllPets,
    findPetForId
};
